Asset.VertexProgram:
VERTEX_OUTPUT main()
{
	export(float3, InputPosition, gVertexInput.Position);

	VERTEX_OUTPUT output;
	output.Normal = gVertexInput.Normal;
	return output;
}

Asset.PixelProgram:
void main(PIXEL_INPUT input, inout PixelOutput output)
{
	export(float3, WorldNormal, input.Normal);
}
